@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

<urn:example:LabelToDCTERMS>
    a sh:NodeShape ;
    sh:targetSubjectsOf rdfs:label ;
    sh:rule [
        a sh:SPARQLRule ;
        sh:construct """
            prefix dcterms: <http://purl.org/dc/terms/>
            prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>
            CONSTRUCT {
                $this dcterms:title ?label
            } WHERE {
                $this rdfs:label ?label
            }
        """
    ] ;
.

@prefix sh: <http://www.w3.org/ns/shacl#> .

<urn:example:custom>
    a sh:NodeShape ;
    sh:targetSubjectsOf <https://example.org/my-bb-model/a> ;
    sh:rule [
        a sh:SPARQLRule ;
        sh:construct """
            CONSTRUCT {
                $this <https://example.org/my-bb-model/c> ?c
            } WHERE {
                $this <https://example.org/my-bb-model/a> ?a .
                BIND(?a + 5 AS ?c)
            }
        """
    ] ;
.
